library(ggmap)
library(maps)
library(ggplot2)



## the eight sites from the previous analysis, with Knysna separate for labelling purposes
Sitecoords <- data.frame(lat=c(-30.209897, -29.522100, -34.049890,-34.144076, -29.259085, -27.557218, -34.023150),
                         lon=c(30.802942, 31.235477, 18.354964, 22.124972, 16.865155, 32.678933, 23.857731), 
                         label=c("Alliwal Shoal", "Ballito", "Hout Bay", "Mossel Bay", "Port Nolloth", "Sodwana","Tsitsikamma"))

Knysna <- data.frame(lat=c(-34.085496), lon = c(23.061299), label = "Knysna")


map <- get_map(location = 'South Africa', zoom = 5, maptype = "hybrid")
SA <- ggmap(map)
SitesMap <- SA +  geom_point(data=Sitecoords, aes(x=lon, y=lat), color="red", size=4, alpha=0.5) +  geom_text(data = Sitecoords, aes(x=lon, y=lat, label=label),size = 3, vjust = -1, hjust = 0.5) + geom_point(data=Knysna, aes(x=lon, y=lat), color="red", size=4, alpha=0.5) + geom_text(data = Knysna, aes(x=lon, y=lat, label=label),size = 3, vjust = 1, hjust = 0.5)  
SitesMap

